def longestPalindrome(s: str) -> str:
    def expandAroundCenter(left: int, right: int) -> str:
        # Expand around center while characters match
        while left >= 0 and right < len(s) and s[left] == s[right]:
            left -= 1
            right += 1
        # Return the palindrome (left and right are now outside the palindrome)
        return s[left + 1:right]

    if len(s) < 2:
        return s

    longest = ""

    for i in range(len(s)):
        # Check odd-length palindromes (single character center)
        odd = expandAroundCenter(i, i)
        if len(odd) > len(longest):
            longest = odd

        # Check even-length palindromes (two character center)
        if i + 1 < len(s):
            even = expandAroundCenter(i, i + 1)
            if len(even) > len(longest):
                longest = even

    return longest
