def group_anagrams(strs: list[str]) -> list[list[str]]:
    # Use a dictionary to group anagrams
    anagram_groups = {}

    for s in strs:
        # Sort the string to create a key for anagrams
        sorted_s = ''.join(sorted(s))

        # Add to the appropriate group
        if sorted_s in anagram_groups:
            anagram_groups[sorted_s].append(s)
        else:
            anagram_groups[sorted_s] = [s]

    # Return all groups
    return list(anagram_groups.values())

# Alternative approach using character count
def group_anagrams_count(strs: list[str]) -> list[list[str]]:
    from collections import defaultdict

    anagram_groups = defaultdict(list)

    for s in strs:
        # Count characters to create a key
        char_count = [0] * 26
        for char in s:
            char_count[ord(char) - ord('a')] += 1

        # Convert count array to tuple for hashing
        key = tuple(char_count)
        anagram_groups[key].append(s)

    return list(anagram_groups.values())
